float PrivateSecondaryStructureDistance(const char* w, const char* v,float ssoffset){
    //This is Hamming Distance with a geometric decay function according to rho
	int lengthOfWord = strlen(w);
    int i;
    float result = 0.0;
    int halflen = lengthOfWord / 2;
    float normalizer = 2 * (1 - pow(ssrho,((lengthOfWord + 1.0) / 2.0))) / (1.0 - ssrho);
    for(i = 0;i < lengthOfWord; i++){
        if(w[i] != v[i]){
            if (i == halflen){
                result += 2;
            }
            else{
                result += pow(ssrho,abs(halflen - i));
            }
        }
    }
    return (float) result * ssoffset / normalizer;
}


float CombinedAlphaDistance(const Pointer p, const Pointer q){
	char *w = *((word*) p);
	char *v = *((word*) q);
    //Split the word into secondary structure chunk and amino acid chunk
    //Then combine the distances in the linear combination
    int wordlen = strlen(w);
    int halflen = wordlen / 2;
    char* aminoacidsw = malloc(halflen + 1);
    char* aminoacidsv = malloc(halflen + 1);
    char* secondaryw = malloc(halflen + 1);
    char* secondaryv = malloc(halflen + 1);
    strncpy(aminoacidsw,w,halflen);
    strncpy(aminoacidsv,v,halflen);
    strncpy(secondaryw,w + halflen,halflen);
    strncpy(secondaryv,v + halflen,halflen);
    aminoacidsw[halflen] = '\0';
    aminoacidsv[halflen] = '\0';
    secondaryw[halflen] = '\0';
    secondaryv[halflen] = '\0';
    float result = epsilon * PrivatewordDist2Alpha(aminoacidsw,aminoacidsv) + (1.0 - epsilon) * PrivateSecondaryStructureDistance(secondaryw,secondaryv,amaximum);
    
    free(aminoacidsw);
    free(aminoacidsv);
    free(secondaryw);
    free(secondaryv);
    return result;

}

