Pointer WordReader( FILE* datastream, int numPts ){
  word* data = malloc( sizeof(word) * numPts );

  if( data == NULL ){
    fprintf( stderr, "Unable to allocate memory for data points. Aborting\n");
    Halt();
  }

  int ii;
  for( ii = 0; ii < numPts; ii++ ){
    fscanf( datastream, "%s", data[ ii ] );
    //printf("word %s\n",data[ii]);
  }

  return data;
}

